var fs = require("fs");
var xss = require("xss");

//有可能不是C盘
var sysHostPath = 'C:/Windows/System32/drivers/etc/hosts';

function PHost(){}

PHost.prototype = {
	getSysHost : function(){
		return fs.readFileSync(sysHostPath);
	},

	setSysHost : function(data){
		fs.writeFileSync(sysHostPath, data);
	},
	
	filterXss : function(data){
		data = data.replace(/[<>'"]/g, '');
		return xss(data);
	}
}


exports.PHost = new PHost();

